#' calJSI
#'
#' @description The Jaccard similarity coefficient is defined as the ratio of shared variants to all
#' (shared plus discordant) variants for two samples.
#'
#' @param maf a Maf object generated by readMaf function
#' @param patient.id select the specific patients. Default: NULL, all patients are included
#' @param plot logical, whether to show the plot, default TRUE
#' @param use.circle, whether to use circle as background of the correlation matrix plot, 
#' default TURE
#' @param title the title of the plot, default is "Jaccard similarity"
#'
#' @examples
#' calJSI(maf)
#' @return correlation matrix and heatmap via Jaccard similarity coefficient method
#' @export calJSI

JSI_dist <- function(df){
    df <- tidyr::pivot_wider(df,
            names_from = Tumor_Sample_Barcode,
            values_from = VAF,
            values_fill = list(VAF = 0)
    ) %>%
    dplyr::select(-Patient_ID, -mutation_id)

    dist <- diag(1, nrow = ncol(df), ncol = ncol(df))
    for (i in 1:(ncol(df) - 1)) {
        for (j in (i + 1):ncol(df)) {
            pair <- data.frame(df[,i], df[,j]) %>%
                dplyr::filter(rowSums(.)!=0) %>%
                `colnames<-`(c("s1", "s2"))

            comm <- pair %>%
                dplyr::filter(., s1>0 & s2>0)

            dist[i, j] <- dist[j, i] <- nrow(comm)/nrow(pair)  
        }
    }
    rownames(dist) <- colnames(df)
    colnames(dist) <- colnames(df)

    return(JSI.dist =  dist)
    }    

calJSI <- function(maf, patient.id = NULL, plot = TRUE, 
    use.circle = TRUE, title = "Jaccard similarity") {

    mafData <- maf@data

    if(is.null(patient.id)){
        patient.id = unique(mafData$Patient_ID)
    }else{
        patient.setdiff <- setdiff(patient.id, unique(mafData$Patient_ID))
        if(length(patient.setdiff) > 0){
            stop(paste0("Patient ", patient.setdiff, " can not be found in your data"))
        }
    }

    JSI.dist <- mafData %>%
        tidyr::unite(
            "mutation_id",
            c(
                "Chromosome",
                "Start_Position",
                "Reference_Allele",
                "Tumor_Seq_Allele2"
            ),
            sep = ":",
            remove = FALSE
        ) %>%
        dplyr::select(
            mutation_id,
            Tumor_Sample_Barcode,
            Patient_ID,
            VAF) %>% 
        dplyr::group_by(Patient_ID) %>%
        dplyr::group_map(~JSI_dist(.), keep = TRUE) %>%
        rlang::set_names(patient.id)

    JSI.plot = list()
    if(plot){
        for(i in 1:length(JSI.dist)){
            JSI.plot[[i]] <- plotCorr(
                JSI.dist[[i]], 
                use.circle = use.circle,
                title = paste0(title, " of patient ", patient.id[i])
                )
        }
        names(JSI.plot) <- patient.id
    }

   return(list(JSI.dist = JSI.dist, JSI.plot =  JSI.plot))  

}

