#' @title ccfPair
#' @description Density plot of CCFs in paired samples of the patient
#' This function requires CCF for clustering
#' @param maf a Maf object generated by readMaf function.
#' @param patient.id select the specific patients. Default: NULL, all patients are included
#' @param min.ccf the minimum value of CCF. Default: 0
#' @param show.density logical. If TRUE, the default, perform density estimation.
#' 
#' @return
#' @examples
#' ccfPair(maf, show.density = TRUE)
#' @export ccfPair


compareCCF <- function(mafData, pairByType = FALSE, geneList = NULL, show.density = TRUE){
   
  patientid <- unique(mafData$Patient_ID)
  
  if(pairByType){
      types <- unique(mafData$Tumor_Type)
      
      if(length(types) < 2){
          message(paste0("Warnings: Only one tumor type was found of ",patientid,". It you want to compare CCF between regions, pairByType should be set as FALSE"))
          return(NA)
      }
      
      ## get average CCF
      mafData <- tidyr::unite(
          mafData,
          "mutID",
          c(
              "Patient_ID",
              "Tumor_Type",
              "Chromosome",
              "Start_Position"
          ),
          sep = ":",
          remove = FALSE
      ) %>%
          dplyr::mutate(totalDepth = Alt_allele_depth + Ref_allele_depth) 
          dplyr::filter(!is.na(CCF),!is.na(totalDepth)) %>%
          dplyr::group_by(mutID) %>% 
          dplyr::mutate(CCF = round(sum(CCF * Alt_allele_depth)/sum(Alt_allele_depth),3)) %>%
          dplyr::ungroup() %>% 
          data.table::as.data.table()
      
      pairs <- combn(length(types), 2, simplify = FALSE)  
  }
  else{
      samples <- unique(mafData$Tumor_Sample_Barcode)
      if(length(samples) < 2){
          message(paste0(unique(mafData$Patient_ID), " have less than 2 samples, return NA"))
          return(NA)
      }
      
      pairs <- combn(length(samples), 2, simplify = FALSE)  
  }
  
  p <- list()
  p.name <- c()
  i <- 1
  for (pair in pairs){
    if(pairByType){
        S1 <- types[pair[1]]
        S2 <- types[pair[2]]  
    }
    else{
        S1 <- samples[pair[1]]
        S2 <- samples[pair[2]]
    }
    if(pairByType){
        ccf.pair <- mafData %>% dplyr::filter(Tumor_Type %in% c(S1, S2)) %>%
            tidyr::unite("mutation_id", c("Chromosome", "Start_Position", "Reference_Allele", "Tumor_Seq_Allele2"), 
                         sep = ":", remove = FALSE) %>% 
            tidyr::unite("mutation_id2", c("Tumor_Type","Chromosome", "Start_Position", "Reference_Allele", "Tumor_Seq_Allele2"), 
                         sep = ":", remove = FALSE) %>% 
            dplyr::distinct(mutation_id2, .keep_all = TRUE) %>%
            dplyr::select(Tumor_Type, Hugo_Symbol, mutation_id, CCF) %>% 
            tidyr::spread(key = Tumor_Type, value = CCF) %>% 
            dplyr::rename("sample1" = S1, "sample2" = S2) %>% 
            dplyr::filter(!is.na(sample1),!is.na(sample2))
        
    }
    else{
        ccf.pair <- mafData %>% dplyr::filter(Tumor_Sample_Barcode %in% c(S1, S2)) %>%
            tidyr::unite("mutation_id", c("Chromosome", "Start_Position", "Reference_Allele", "Tumor_Seq_Allele2"), 
                         sep = ":", remove = FALSE) %>% 
            dplyr::select(Tumor_Sample_Barcode, Hugo_Symbol, mutation_id, CCF) %>% 
            tidyr::spread(key = Tumor_Sample_Barcode, value = CCF) %>% 
            dplyr::rename("sample1" = S1, "sample2" = S2) %>% 
            dplyr::filter(!is.na(sample1),!is.na(sample2)) 
    }
    
    if(nrow(ccf.pair) == 0){
        message(paste0("Warning: No shared mutaions were detected between ",S1, " and ", S2) )
        p[[i]] <- NA
        next()
    }
    # my.cols <- colorRampPalette(c(
    #                               "#bcbddc",
    #                               "#9e9ac8",
    #                               "#807dba",
    #                               "#fc9272",
    #                               "#ef3b2c"), space="rgb")  
    # ccf.pair$d <- densCols(ccf.pair$sample1,
    #                        ccf.pair$sample2,
    #                        colramp = my.cols)
    # p[[i]] <- ggplot(ccf.pair) +
    #     geom_point(aes(sample1, sample2, col = d), size = 1) +
    #     scale_color_identity() +
    #     theme_bw()
    p[[i]] <- ggplot2::ggplot(data= ccf.pair, aes(x=sample1, y=sample2)) +
        xlab(S1) +
        ylab(S2) +
      theme(
        # axis.text = element_text(size = 12, color = "black"),
        axis.text = element_text(size = 10, color = "black"),
        axis.title = element_text(size = 13, color = "black"),
        legend.text = element_text(size = 10),
        legend.title = element_text(size = 12),

        axis.line.x.top = element_blank(),
        axis.line.y.right = element_blank(),
        axis.ticks.length.y.right = element_blank(),
        axis.ticks.length.x.top = element_blank(),
        panel.background = element_rect(color = "black",size = 0.5),
        #panel.background = element_rect(colour = "black", size = 0.4),
        panel.grid.major = element_blank(),
        panel.grid.minor = element_blank(),
        plot.title = element_text(hjust = 0.5)
        )+
      guides(shape = guide_legend(override.aes = list(size = 0.2))) +
      # theme_bw() +
      coord_fixed() +
      scale_x_continuous(expand = c(0,0), limits = c(0,1))+
        scale_y_continuous(expand = c(0,0), limits = c(0,1))
      # labs(x = S1, y = S2) +
    if(pairByType){
       p[[i]] <- p[[i]] + ggtitle(paste("CCF density plot in paired tumor types:\n ", S1, " vs ", S2, sep = ""))

    }
    else{
        p[[i]] <- p[[i]] + ggtitle(paste("CCF density plot in paired samples:\n ", S1, " vs ", S2, sep = ""))
    }

    if(show.density){
      p[[i]] <- p[[i]] + stat_density_2d(aes(fill = ..density..), geom = 'tile',contour = FALSE) +
        # scale_fill_gradient(low = "#8491B499",high = "#E64B35FF")
          scale_fill_gradientn(colours = c("white",
                                           "#bcbddc",
                                           "#9e9ac8",
                                           "#807dba",
                                           "#fc9272",
                                           "#ef3b2c"),
                               name = "Density")
    }
    p[[i]] <- p[[i]] + geom_point(size = 0.5)
    # use smoothscatter
    # ccf.pair.matrix <- matrix(c(ccf.pair$sample1, ccf.pair$sample2),ncol = 2)
    # den.col <- colorRampPalette(c("white","#8491B499", "#E64B35FF"))
    # smoothScatter(ccf.pair.matrix,
    #               colramp = den.col,
    #               xlab = S1,
    #               ylab = S2,
    #               main = paste("CCF density plot in paired samples:\n ", S1, " vs ", S2, sep = ""),
    #               legend.plot = T)

    ## label muation on geneList
    if(!is.null(geneList)){
        ## set table for gene
        genes.table <- ccf.pair %>% 
            dplyr::rowwise() %>% 
            dplyr::filter(any(strsplit(Hugo_Symbol,",|;")[[1]] %in% geneList)) %>%
            as.data.table()
        
        if(nrow(genes.table) > 0){
            genes.name <- unlist(lapply(genes.table$Hugo_Symbol, function(x){
                ns <- strsplit(x,",|;")[[1]]
                idx <- which(ns %in% geneList)[1]
                return(ns[idx])
            }))
            genes.table$Hugo_Symbol <- genes.name
            p[[i]] <- p[[i]] +
                ## add point
                geom_point(data = genes.table,
                           aes(x = sample1, y = sample2),
                           shape = 24,
                           size = 2,
                           fill = "red")+
                ## label gene
                geom_text_repel(data = genes.table,
                          aes(x = sample1, y = sample2, label = Hugo_Symbol),
                          size = 3.5,
                          force = 10)
            
            ## smoothscatter, add gene names
            # points(genes.table[,.(sample1,sample2)],cex=0.6,col=2,pch=2)
            # text(genes.table[,.(sample1,sample2)],cex=0.7,pos=1,genes.table$Hugo_Symbol)
        }
        else{
            message(paste0("Warning: None of genes map to pair samples ",S1, " vs ", S2,) )
        }
    }
    p.name <- c(p.name, paste(S1,"-",S2, sep = ""))
    i =  i+1
  }
  
  names(p) <- p.name
  return(p)  
}


ccfPair <- function(maf,
                    patient.id = NULL,
                    pairByType = FALSE,
                    geneList = NULL,
                    min.ccf = 0,
                    show.density = TRUE){
  mafData <- maf@data

  ## check if ccf data is provided
  if(! "CCF" %in% colnames(mafData)){
    stop(paste0("ccfDensity function requires CCF data.\n",
    "No CCF data was found when generate Maf object."))
  }

  
  if(is.null(patient.id)){
      
      patient.id = unique(mafData$Patient_ID)
  
  }else{
      
      patient.setdiff <- setdiff(patient.id, unique(mafData$Patient_ID))
      
      if(length(patient.setdiff) > 0){
          
          stop(paste0("Patient ", patient.setdiff, " can not be found in your data"))
      
      }
  
  }

  
  density.list <- mafData %>%
      dplyr::filter(Patient_ID %in% patient.id & CCF > min.ccf) %>%
      dplyr::group_by(Patient_ID) %>%
      dplyr::group_map(., ~compareCCF(.x, pairByType = pairByType, geneList = geneList, show.density = show.density), keep=TRUE) %>%
      rlang::set_names(patient.id)

  return(density.list)      
}
